mapper.select:
SELECT f.flight_id, f.flight_number, f.departure_time, f.arrival_time, f.flight_time, f.departure_airport, f.arrival_airport, f.user_id, f.flight_price, f.flight_status, a.airline_name FROM flight f JOIN airline a ON f.user_id = a.user_id
AND f.departure_airport = #{departureAirport}
AND f.arrival_airport = #{arrivalAirport}
AND f.departure_time >= TO_DATE(#{departureTime} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') AND f.departure_time < TO_DATE(#{departureTime} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
ORDER BY
${orderItem}
ORDER BY f.departure_time ASC

SELECT COUNT(*) FROM flight f JOIN airline a ON f.user_id = a.user_id
AND f.departure_airport = #{departureAirport}
AND f.arrival_airport = #{arrivalAirport}
AND f.departure_time >= TO_DATE(#{departureTime} || ' 00:00:00', 'YYYY-MM-DD HH24:MI:SS') AND f.departure_time < TO_DATE(#{departureTime} || ' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
